mapper.select:
select
im.from_id, im.to_id, im.content, im.create_time, iu.avatar from_user_avatar, iu.name from_nickname, im.type
from (
select from_id, to_id, content, create_time, type from im_message where type = 1

    and create_time  <  #{dto.timestamp,jdbcType=TIMESTAMP}

and (from_id = #{dto.fromId,jdbcType=VARCHAR} AND to_id = #{dto.toId,jdbcType=VARCHAR}
OR from_id = #{dto.toId,jdbcType=VARCHAR} AND to_id = #{dto.fromId,jdbcType=VARCHAR})
ORDER BY create_time desc LIMIT #{dto.count,jdbcType=INTEGER}) im
left join im_user iu on im.from_id = iu.id

select
im.from_id, im.to_id to_group_id, im.content, im.create_time, iu.avatar from_user_avatar, iu.name from_nickname, im.type
from (
select from_id, to_id, content, create_time, type from im_message where type = 3

    and create_time  <  #{dto.timestamp,jdbcType=TIMESTAMP}

 AND to_id = #{dto.toId,jdbcType=VARCHAR}
ORDER BY create_time desc LIMIT #{dto.count,jdbcType=INTEGER}) im
left join im_user iu on im.from_id = iu.id
